import React from "react";
import { SwipeableDrawer, List, ListItem, Divider } from "@mui/material";
import ListItemText from "@mui/material/ListItemText";
import IconButton from "@mui/material/IconButton";
import MenuIcon from "@mui/icons-material/Menu";

import { Link } from "react-router-dom";

// styling
import { makeStyles } from "@mui/styles";
import theme from "./theme";

const useStyles = makeStyles(() => ({
  drawerMenuHolder: {
    backgroundColor: theme.palette.common.blue,
  },
  menuIcon: {
    color: "white",
  },
  iconMenuBtn: {
    "&.MuiIconButton-root": {
      marginLeft: "auto",

      "&:hover": {
        backgroundColor: "transparent",
      },
    },
  },
}));

const MainDrawerMenu: React.FC = () => {
  const [isDrawerOpen, setIsDrawerOpen] = React.useState(false);
  const classes = useStyles();

  // Responsive swipe on mobile
  const iOS =
    typeof navigator !== "undefined" &&
    /iPad|iPhone|iPod/.test(navigator.userAgent);

  return (
    <React.Fragment>
      <SwipeableDrawer
        classes={{ paper: classes.drawerMenuHolder }}
        disableBackdropTransition={!iOS}
        disableDiscovery={iOS}
        open={isDrawerOpen}
        onClose={() => {
          setIsDrawerOpen(false);
        }}
        onOpen={() => {
          setIsDrawerOpen(true);
        }}>
        <List disablePadding>
          <ListItem
            button
            component={Link}
            to='/'
            onClick={() => {
              setIsDrawerOpen(false);
            }}>
            <ListItemText disableTypography>Home</ListItemText>
          </ListItem>
          <ListItem
            button
            component={Link}
            to='/services'
            onClick={() => {
              setIsDrawerOpen(false);
            }}>
            <ListItemText disableTypography>Services</ListItemText>
          </ListItem>
          <ListItem
            button
            component={Link}
            to='/revolution'
            onClick={() => {
              setIsDrawerOpen(false);
            }}>
            <ListItemText disableTypography>Revolution</ListItemText>
          </ListItem>
          <ListItem
            button
            component={Link}
            to='/about'
            onClick={() => {
              setIsDrawerOpen(false);
            }}>
            <ListItemText disableTypography>About</ListItemText>
          </ListItem>
          <ListItem
            button
            component={Link}
            to='/contact'
            onClick={() => {
              setIsDrawerOpen(false);
            }}>
            <ListItemText disableTypography>Contact Us</ListItemText>
          </ListItem>
          <Divider />
          <ListItem
            button
            component={Link}
            to='/estimate'
            onClick={() => {
              setIsDrawerOpen(false);
            }}>
            <ListItemText disableTypography>Estimate</ListItemText>
          </ListItem>
        </List>
      </SwipeableDrawer>
      <IconButton
        className={classes.iconMenuBtn}
        onClick={() => {
          setIsDrawerOpen(!isDrawerOpen);
        }}>
        <MenuIcon fontSize='large' className={classes.menuIcon} />
      </IconButton>
    </React.Fragment>
  );
};

export default MainDrawerMenu;
