import React from "react";
import { makeStyles } from "@mui/styles";
import { Grid, Typography } from "@mui/material";
import useMediaQuery from "@mui/material/useMediaQuery";
import vision from "../../assets/vision.svg";

import theme from "./theme";

const useStyle = makeStyles(() => ({
  title: {
    "&.MuiTypography-root": {
      ...theme.typography.revolutionTitle,
    },
  },
  subTitle: {
    "&.MuiTypography-root": {
      ...theme.typography.titles,
      lineHeight: 1.2,
      marginBottom: "1em",
    },
  },
}));

const IosAndroidAppPage: React.FC = () => {
  const classes = useStyle();
  const matchesSml = useMediaQuery(theme.breakpoints.down("sm"));
  const matchesMd = useMediaQuery(theme.breakpoints.down("md"));

  return (
    <Grid
      container
      direction='column'
      sx={{ padding: matchesSml ? "1em" : "2em 4em" }}>
      <Grid item>
        <Typography className={classes.title}>the revolution</Typography>
      </Grid>
      <Grid item>
        <Grid container>
          <Grid item>
            <img src={vision} alt='montain' style={{ width: "400px" }} />
          </Grid>
          <Grid item>
            <Grid container direction={"column"}>
              <Typography className={classes.subTitle}> Vision</Typography>
            </Grid>
            <Grid container direction={"column"}>
              <Typography paragraph>
                The rise of computers, and subsequently the Internet, has
                completely altered every aspect of human life. This has
                increased our comfort, broadened our connections, and reshaped
                how we view the world.
              </Typography>
              <Typography paragraph>
                What once was confined to huge rooms and teams of engineers now
                resides in every single one of our hands. Harnessing this
                unlimited potential by using it to solve problems and better
                lives is at the heart of everything we do.
              </Typography>
              <Typography paragraph>
                We want to help businesses capitalize on the latest and greatest
                technology. The best way to predict the future is to be the one
                building it, and we want to help guide the world into this next
                chapter of technological expansion, exploration, and innovation.
              </Typography>
            </Grid>
          </Grid>
        </Grid>
      </Grid>
    </Grid>
  );
};

export default IosAndroidAppPage;
