import React from "react";

import { makeStyles } from "@mui/styles";
import { Grid, Card, CardContent, Typography, Button } from "@mui/material";
import ArrowComponent from "../ArrowComponent";
import theme from "../theme";
import ThumbUpIcon from "@mui/icons-material/ThumbUp";

import infoBackground from "../../../assets/infoBackground.svg";

const useStyle = makeStyles(() => ({
  aboutandcontactbackground: {
    backgroundImage: `url(${infoBackground})`,
    backgroundPosition: "center",
    backgroundSize: "cover",
    backgroundRepeat: "no-repeat",
    height: "100%",
    width: "100%",
  },
  aboutandcontactBtn: {
    "&.MuiButton-root": {
      ...theme.typography.learnMoreBtn,
      marginTop: "0.5em",
      color: "white",
      border: "1px solid white",
    },
  },
  aboutandcontacttitles: {
    "&.MuiTypography-root": {
      fontWeight: "bold",
      fontSize: 25,
      color: "white",
    },
  },
  description: {
    "&.MuiTypography-root": {
      color: "white",
    },
  },
}));

const ContactAndAboutSection: React.FC = () => {
  const classes = useStyle();

  return (
    <Grid
      container
      direction='row'
      sx={{ flexWrap: "wrap", height: "60em" }}
      alignItems='center'>
      <Grid item xs={6} sx={{ position: "absolute", marginLeft: "5em" }}>
        <Grid container direction='column'>
          <Typography className={classes.aboutandcontacttitles}>
            About Us
          </Typography>
          <Typography className={classes.description}>
            Let's get personal.
          </Typography>
          <Button variant='outlined' className={classes.aboutandcontactBtn}>
            Learn More
          </Button>
        </Grid>
      </Grid>

      <div className={classes.aboutandcontactbackground} />
    </Grid>
  );
};

export default ContactAndAboutSection;
