import React, { useEffect } from "react";
import Header from "./ui/header";
import { ThemeProvider } from "@mui/material/styles";

import theme from "./ui/theme";
import { CssBaseline } from "@mui/material";
import { BrowserRouter, Route, Routes } from "react-router-dom";
import Footer from "./ui/footer";

const App: React.FC = (props) => {
  const [value, setValue] = React.useState(0);
  const [selectedIndex, setSelectedIndex] = React.useState(0);

  useEffect(() => {
    switch (window.location.pathname) {
      case "/":
        if (value != 0) {
          setValue(0);
        }
        break;
      case "/services":
        if (value != 1) {
          setValue(1);
        }
        break;
      case "/revolution":
        if (value != 2) {
          setValue(2);
        }
        break;
      case "/about":
        if (value != 3) {
          setValue(3);
        }
        break;
      case "/contact":
        if (value != 4) {
          setValue(4);
        }
        break;
      case "/customsoftware":
        if (value != 1) {
          setValue(1);
        }
        break;
      case "/mobileapps":
        if (value != 1) {
          setValue(1);
        }
        break;
      case "/websites":
        if (value != 1) {
          setValue(1);
        }
        break;
      default:
        break;
    }

    switch (window.location.pathname) {
      case "/services":
        if (value != 1) {
          setSelectedIndex(0);
        }
        break;
      case "/customsoftware":
        if (value != 1) {
          setSelectedIndex(1);
        }
        break;
      case "/customsoftware":
        if (value != 1) {
          setSelectedIndex(1);
        }
        break;
      case "/mobileapps":
        if (value != 1) {
          setSelectedIndex(2);
        }
        break;
      case "/websites":
        if (value != 1) {
          setSelectedIndex(3);
        }
        break;
      default:
        break;
    }
  }, [value]);

  const routeValueHandler = (n: number) => {
    setValue(n);
  };

  const headerModalToolTipSelectedTabHandler = (n: number) => {
    setSelectedIndex(n);
  };

  const toolTipDrawerMenuSelectedLinkHandler = (n: number) => {};

  return (
    <ThemeProvider theme={theme}>
      <BrowserRouter>
        <CssBaseline />
        <Header
          routeValueHandler={{
            routeValueHandler: routeValueHandler,
            routeValue: value,
          }}
        />
        <Routes>
          <Route
            path='/'
            element={<div style={{ height: "2000px" }}>home</div>}
          />
          <Route path='/services' element={<div>services</div>} />
          <Route path='/customsoftware' element={<div>custom software</div>} />
          <Route path='/mobileapps' element={<div>mobile apps</div>} />
          <Route path='/websites' element={<div>websites</div>} />
          <Route path='/revolution' element={<div>revolution</div>} />
          <Route path='/about' element={<div>about</div>} />
          <Route path='/contact' element={<div>contact</div>} />
          <Route path='/estimate' element={<div>estimate</div>} />
        </Routes>
        <Footer />
      </BrowserRouter>
    </ThemeProvider>
  );
};

export default App;
