import React, { useEffect } from "react";
import ToolBar from "@mui/material/Toolbar";
import Tabs from "@mui/material/Tabs";
import Tab from "@mui/material/Tab";
import Button from "@mui/material/Button";
import MenuPositionedToolTip from "./menuToolbar";

import { makeStyles } from "@mui/styles";
import { styled } from "@mui/material/styles";
import theme from "./theme";

import { Link } from "react-router-dom";

// import Typography from "@mui/material/Typography";

// creative components
import logo from "../../assets/logo.svg";

// Styling
const useStyles = makeStyles(() => ({
  logo: {
    height: "5em",
  },
  tabsContainer: {
    marginLeft: "auto",
    color: "white",
  },
  tab: {
    "&.MuiTab-root": {
      ...theme.typography.tab,
      minWidth: 10,
      marginLeft: "25px",
    },
  },
  logoContainer: {
    "&.MuiButton-root": {
      padding: 0,
      "&:hover": {
        backgroundColor: "transparent",
      },
    },
  },
}));

// Styling the tab, need to create a

const StyledButton = styled(Button)(() => ({
  ...theme.typography.estimateBtn,
  borderRadius: "50px",
  marginLeft: "25px",
  marginRight: "25px",
  height: "50px",
}));

const ToolBarCom: React.FC = () => {
  const [value, setValue] = React.useState(0);
  const [anchorEl, setAnchorEl] = React.useState<null | HTMLElement>(null);
  const open = Boolean(anchorEl);
  // handles classes for non react elements
  const classes = useStyles();

  const onChangeHandler = (e: React.SyntheticEvent, value: number) => {
    console.log(value);
    setValue(value);
  };

  useEffect(() => {
    console.log(value);
    if (window.location.pathname === "/" && value != 0) {
      setValue(0);
    } else if (window.location.pathname === "/services" && value !== 1) {
      setValue(1);
    } else if (window.location.pathname === "/revolution" && value !== 2) {
      setValue(2);
    } else if (window.location.pathname === "/about" && value !== 4) {
      setValue(3);
    } else if (window.location.pathname === "/contact" && value !== 3) {
      setValue(4);
    } else if (window.location.pathname === "/estimate" && value !== 5) {
      setValue(5);
    }
  }, [value]);

  const handleClick = (event: React.MouseEvent<HTMLElement>) => {
    setAnchorEl(event.currentTarget);
  };

  const handleClose = () => {
    setAnchorEl(null);
  };

  return (
    <ToolBar disableGutters>
      {/* <Typography variant='h3'>Arc Development</Typography> */}
      <Button
        component={Link}
        to='/'
        disableRipple
        onClick={() => {
          setValue(0);
        }}
        className={classes.logoContainer}>
        <img src={logo} alt='company logo' className={classes.logo} />
      </Button>

      <Tabs
        value={value}
        onChange={onChangeHandler}
        textColor='secondary'
        indicatorColor='primary'
        className={classes.tabsContainer}
        variant='scrollable'
        scrollButtons='auto'>
        <Tab
          component={Link}
          to='/'
          className={classes.tab}
          value={0}
          label='Home'
        />
        <Tab
          id='services-positioned-tab'
          component={Link}
          to='/services'
          className={classes.tab}
          value={1}
          label='Services'
          // handles menu tooltip
          aria-controls={open ? "services-menu" : undefined}
          aria-expanded={open ? "true" : undefined}
          onClick={handleClick}
        />
        <MenuPositionedToolTip
          id='services-positioned-tab'
          menu_id='services-menu'
          open={open}
          anchorElement={anchorEl}
          closeMenu={handleClose}
        />
        <Tab
          component={Link}
          to='/revolution'
          className={classes.tab}
          value={2}
          label=' The Revolution'
        />
        <MenuPositionedToolTip
          id='revolution-positioned-tab'
          menu_id='revolution-menu'
          open={open}
          anchorElement={anchorEl}
          closeMenu={handleClose}
        />
        <Tab
          component={Link}
          to='/about'
          className={classes.tab}
          value={3}
          label='About Us'
        />
        <Tab
          component={Link}
          to='/contact'
          className={classes.tab}
          value={4}
          label=' Contact Us'
        />
      </Tabs>
      <StyledButton variant='contained' color='secondary'>
        Free Trial
      </StyledButton>
    </ToolBar>
  );
};

export default ToolBarCom;
