import { createTheme } from "@mui/material/styles";

import "@mui/material/styles/createPalette";
import "@mui/material/styles/createTypography";

enum themePalette {
  ARCBLUE = "#0B72B9",
  ARCORANGE = "#FFBA60",
}

enum typographyFonts {
  H3 = 300,
}

// Creating new attributes to existing styling
declare module "@mui/material/styles/createPalette" {
  interface CommonColors {
    blue: string;
    orange: string;
  }
}

declare module "@mui/material/styles/createTypography" {
  interface TypographyOptions {
    tab?: React.CSSProperties;
  }
}

declare module "@mui/material/styles" {
  interface Theme {
    // status: {
    //   danger: string;
    // };
  }
  // allow configuration using `createTheme`
  interface ThemeOptions {
    // status?: {
    //   danger?: string;
    // };
  }
}

// adding a field

const theme = createTheme({
  // status:{
  //   danger: themePalette.ARCORANGE,
  // },
  palette: {
    primary: {
      main: themePalette.ARCBLUE,
    },
    secondary: {
      main: themePalette.ARCORANGE,
    },
    common: {
      blue: themePalette.ARCBLUE,
      orange: themePalette.ARCORANGE,
    },
  },
  // components: {
  //   MuiAppBar: {
  //     defaultProps: {
  //       style: {
  //         textTransform: "uppercase",
  //       },
  //     },
  //   },
  // },
  typography: {
    tab: {
      color: "white",
      fontFamily: "Raleway",
      textTransform: "none",
      fontWeight: "700",
      fontSize: "1rem",
      minWidth: 10,
    },
    h3: {
      fontWeight: typographyFonts.H3,
      // color: themePalette.ARCBLUE,
    },
  },
});

export default theme;
