import React, { useEffect } from "react";
import { Menu, MenuItem } from "@mui/material";

interface PropsPositionedMenu {
  id: string;
  element: any;
  open: any;
}

const MenuPositionedToolTip: React.FC<PropsPositionedMenu> = ({
  id,
  element,
  open,
}) => {
  const [anchorEl, setAnchorEl] = React.useState<null | HTMLElement>(null);

  // handles the anchorElement event

  const handleClose = () => {
    setAnchorEl(null);
  };

  return (
    <Menu
      id='demo-positioned-menu'
      aria-labelledby={id}
      open={open}
      anchorEl={element}
      anchorOrigin={{
        vertical: "top",
        horizontal: "left",
      }}
      transformOrigin={{
        vertical: "top",
        horizontal: "left",
      }}>
      <MenuItem onClick={handleClose}>test 1</MenuItem>
      <MenuItem onClick={handleClose}>test 2</MenuItem>
      <MenuItem onClick={handleClose}>test 3</MenuItem>
    </Menu>
  );
};

export default MenuPositionedToolTip;
