import React from "react";
import { makeStyles } from "@mui/styles";
import { Grid, Typography } from "@mui/material";
import useMediaQuery from "@mui/material/useMediaQuery";
import LearnMoreButton from "./aux-components/learnMoreButton";
import customSoftwareIcon from "../../assets/Custom Software Icon.svg";
import mobileAppsIcon from "../../../assets/mobileIcon.svg";
import websitesIcon from "../../../assets/websiteIcon.svg";

import theme from "./theme";

const useStyle = makeStyles(() => ({
  service: {
    "&.MuiGrid-item": {
      marginTop: "0.6em",
    },
  },
  icon: {
    width: "9em",
    [theme.breakpoints.down("sm")]: {
      marginBottom: "3em",
    },
  },
  titleService: {
    "&.MuiTypography-root": {
      ...theme.typography.titles,
      lineHeight: 1.2,
    },
  },
  subTitle: {
    "&.MuiTypography-root": {
      marginBottom: "1em",
      color: theme.palette.common.grey,
    },
  },
  servicesPunchLine: {
    "&.MuiTypography-root": {
      lineHeight: 1.2,
      color: theme.palette.common.grey,
      [theme.breakpoints.down("sm")]: {
        marginBottom: "2em",
      },
    },
  },
}));

interface ServicePageProps {
  routerValueHandler: (n: number) => void;
  toolTipValueHandler: (n: number | undefined) => void;
}

const ServicesPage: React.FC<ServicePageProps> = ({
  routerValueHandler,
  toolTipValueHandler,
}) => {
  const classes = useStyle();
  const matches = useMediaQuery(theme.breakpoints.down("sm"));

  return (
    <Grid
      container
      direction='column'
      sx={{ padding: "2em 1em" }}
      textAlign={matches ? "center" : "inherit"}>
      <Grid item>
        <Typography
          variant='h2'
          sx={{ marginBottom: matches ? "2em" : "inherite" }}>
          Services
        </Typography>
      </Grid>
      <Grid item>
        <Grid
          container
          justifyContent={matches ? "center" : "flex-end"}
          textAlign={matches ? "center" : "inherit"}
          alignItems='center'
          spacing={2}>
          <Grid item>
            <Grid container direction='column'>
              {matches ? (
                <Grid item>
                  <img
                    alt='custom software image'
                    src={customSoftwareIcon}
                    className={classes.icon}
                  />
                </Grid>
              ) : null}
              <Grid item>
                <Typography className={classes.titleService}>
                  Custome Software Development
                </Typography>
              </Grid>
            </Grid>
            <Grid item>
              <Typography className={classes.subTitle}>
                Save Energy, Save Time, Save Money
              </Typography>
            </Grid>
            <Grid item>
              <Typography className={classes.servicesPunchLine}>
                Complete digital solutions, from investigation
                {matches ? null : <br />} to
                <span
                  style={{
                    marginLeft: "0.2em",
                    fontFamily: "Pacifico",
                    color: theme.palette.common.orange,
                    fontSize: 21,
                  }}>
                  calibration
                </span>
              </Typography>
            </Grid>

            <Grid item className={classes.service}>
              <LearnMoreButton
                path='/customsoftware'
                routeValueHandler={routerValueHandler}
                toolTipValueHandler={toolTipValueHandler}
                toolTipValue={1}
                routeValue={1}
                height={matches ? 50 : 33}
                width={140}
              />
            </Grid>
          </Grid>
          {matches ? null : (
            <Grid item>
              <img
                alt='custom software image'
                src={customSoftwareIcon}
                className={classes.icon}
              />
            </Grid>
          )}
        </Grid>
      </Grid>
    </Grid>
  );
};

export default ServicesPage;
