import { createTheme } from "@mui/material/styles";

declare module "@mui/material/styles" {
  interface Theme {
    palette: {
      common: {
        blue: string;
        orange: string;
      };
      primary: {
        main: string;
      };
      secondary: {
        main: string;
      };
    };
  }
  // allow configuration using `createTheme`
  interface ThemeOptions {
    paletteOptions?: {
      common?: {
        blue?: string;
        orange?: string;
      };
      primary?: {
        main?: string;
      };
      secondary?: {
        main?: string;
      };
    };
  }
}

// color variables
const arcBlue = "#0B72B9";
const arcOrange = "#FFBA60";

const theme = createTheme({
  paletteOptions: {
    common: {
      blue: arcBlue,
      orange: arcOrange,
    },
    primary: {
      main: arcBlue,
    },
    secondary: {
      main: arcOrange,
    },
  },
});

export default theme;
