import React from "react";

import { makeStyles } from "@mui/styles";
import { Grid, Typography, Button, useMediaQuery } from "@mui/material";
import EstimateButton from "../EstimateButton";
import ArrowComponent from "../ArrowComponent";
import theme from "../theme";

import callToActionBackground from "../../../assets/background.jpg";

const useStyle = makeStyles(() => ({
  aboutandcontactbackground: {
    backgroundImage: `url(${callToActionBackground})`,
    backgroundPosition: "center",
    backgroundSize: "cover",
    backgroundRepeat: "no-repeat",
    height: "100%",
    width: "100%",
  },
  callToActionTitle: {
    "&.MuiTypography-root": {
      ...theme.typography.titles,
      lineHeight: 1,
    },
  },
  callToActionDescription: {
    "&.MuiTypography-root": {
      ...theme.typography.h4,
      lineHeight: 1,
      marginTop: "0.5em",
    },
  },
  learnMoreBtn: {
    "&.MuiButtonBase-root": {
      ...theme.typography.learnMoreBtn,
      marginTop: "1em",
      border: "2px solid",
      "&:hover": {
        border: "2px solid",
      },
    },
  },
}));

const CallToActionSection: React.FC = () => {
  const classes = useStyle();
  //   const matchesSM = useMediaQuery(theme.breakpoints.down("sm"));

  return (
    <Grid container sx={{ height: "50em" }}>
      <Grid
        container
        direction='row'
        sx={{
          position: "absolute",
          padding: "2em",
          height: "50em",
        }}>
        <Grid
          item
          xs={6}
          container
          direction='column'
          justifyContent='center'
          alignItems='flex-end'>
          <Grid item sx={{ textAlign: "left" }}>
            <Typography className={classes.callToActionTitle}>
              Simple Software.
              <br /> Revolutionary Results.
            </Typography>
            <Typography className={classes.callToActionDescription}>
              Take advantage of the <br /> 21st Century
            </Typography>
            <Button variant='outlined' className={classes.learnMoreBtn}>
              <span>Learn more</span>
              <ArrowComponent
                width={10}
                height={10}
                fill={theme.palette.common.blue}
              />
            </Button>
          </Grid>
        </Grid>
        <Grid
          item
          container
          xs={6}
          direction='column'
          justifyContent='center'
          alignItems='flex-end'>
          <EstimateButton clickHandler={() => {}} />
        </Grid>
      </Grid>

      <div className={classes.aboutandcontactbackground} />
    </Grid>
  );
};

export default CallToActionSection;
