import React, { useEffect } from "react";
import { Menu, MenuItem } from "@mui/material";
import { Link } from "react-router-dom";
import { makeStyles } from "@mui/styles";
import theme from "./theme";

// styling

const useStyles = makeStyles(() => ({
  menu: {
    "&.MuiMenu-paper": {
      backgroundColor: theme.palette.common.blue,
      color: "white",
      borderRadius: "0px",
    },
  },
  menuTab: {
    "&.MuiMenuItem-root": {
      opacity: 0.7,
      ...theme.typography.tab,
      "&:hover": {
        opacity: 1,
      },
    },
    "&.Mui-selected": {
      ...theme.typography.tab,
      opacity: 1,
    },
  },
}));

interface ProsMenuPositioned {
  id: string;
  menu_id: string;
  anchorElement: HTMLElement | null;
  open: boolean;
  tabValue: number;
  closeMenu: () => void;
  setValueMenuItem: (index: number) => void;
}

const MenuPositionedToolTip: React.FC<ProsMenuPositioned> = ({
  anchorElement,
  open,
  id,
  menu_id,
  tabValue,
  closeMenu,
  setValueMenuItem,
}) => {
  const [selectedIndex, setSelectedIndex] = React.useState(0);
  const classes = useStyles();

  const menuOptions1: Array<{
    name: string;
    link: string;
    activeIndex: number;
    selectedIndex?: number;
  }> = [
    { name: "Services", link: "/services", activeIndex: 1, selectedIndex: 0 },
    {
      name: "Custome Software Dev",
      link: "/customsoftware",
      activeIndex: 1,
      selectedIndex: 1,
    },
    {
      name: "Mobile Apps Dev",
      link: "/mobileapps",
      activeIndex: 1,
      selectedIndex: 2,
    },
    {
      name: "Website Dev",
      link: "/websites",
      activeIndex: 1,
      selectedIndex: 3,
    },
  ];

  const routes: Array<{
    name: string;
    link: string;
    activeIndex: number;
    selectedIndex?: number;
  }> = [
    { name: "Home", link: "/", activeIndex: 0 },
    { name: "Services", link: "/services", activeIndex: 1 },
    { name: "The Revolution", link: "/revolution", activeIndex: 2 },
    { name: "About Us", link: "/about", activeIndex: 3 },
    { name: "Contact Us", link: "/contact", activeIndex: 4 },
  ];

  useEffect(() => {
    [...menuOptions1, ...routes].forEach((r) => {
      switch (window.location.pathname) {
        case r.link:
          if (tabValue != r.activeIndex) {
            setSelectedIndex(r.activeIndex);
          }
          break;
        default:
          setSelectedIndex(0);
      }
    });
  }, [selectedIndex]);

  const elementClickedHandler = (
    e: React.MouseEvent<HTMLElement>,
    i: number
  ) => {
    setSelectedIndex(i);
    closeMenu();
    setValueMenuItem(i);
  };

  return (
    <Menu
      id={menu_id}
      classes={{ paper: classes.menu }}
      anchorEl={anchorElement}
      open={open}
      aria-labelledby={id}
      anchorOrigin={{
        vertical: "top",
        horizontal: "left",
      }}
      transformOrigin={{
        vertical: "top",
        horizontal: "left",
      }}
      MenuListProps={{ onMouseLeave: closeMenu }}
      elevation={0}>
      {menuOptions1.map((item, index) => {
        return (
          <MenuItem
            key={index}
            component={Link}
            to={item.link}
            className={classes.menuTab}
            selected={index === selectedIndex}
            onClick={(e) => {
              elementClickedHandler(e, index);
            }}>
            {item.name}
          </MenuItem>
        );
      })}
    </Menu>
  );
};

export default MenuPositionedToolTip;
